import { createSlice } from '@reduxjs/toolkit';
import { addQuizToTopic } from '../topics/topicsSlice';


const quizzesSlice = createSlice({
    name: 'quizzes',
    initialState: {
        quizzes: {},
    },
    reducers: {
        addQuiz: (state, action) => {
            const { quizId, name, topicId, cardIds } = action.payload;
            state.quizzes[quizId] = { id: quizId, name, topicId, cardIds };
        },
    },
});

// addQuizToTopic is a thunk action that will be dispatched after the quiz is added
quizzesSlice.actions.addQuizToTopic = (quizId, topicId) => (dispatch) => {
    dispatch(addQuiz(quizId));
    dispatch(addQuizToTopic({ id: quizId, topicId }));
};

// selector
export const selectQuizzes = (state) => state.quizzes.quizzes;

// actions + reducers
export const { addQuiz } = quizzesSlice.actions;
export default quizzesSlice.reducer;
